import React, {useContext} from 'react';
import styled, {css} from 'styled-components';

import {productsContext} from '../../../context/products';
import {SingleProductListItem} from './SingleProductsListItem';

export const SingleProduct = ({history: {push}, match: {params: {id}}}) => {
    const productsList = useContext(productsContext);
    const product = productsList.find(item => item.id == id);
    return(
        <Container>
            {product && ( 
            <Popup border={product.color}>
                <button onClick={() => push('/products')}>X</button>
                <img src={product.image} alt={product.name}/>
                <h1>{product.name}</h1>
                <h2>{product.price}</h2>
                <p>{product.desc}</p>
            </Popup>
            )}
        </Container>
    )
}

const Container = styled.div`
    position: fixed;
    background: ;
    display: flex;rgba(0,0,0,0.3)
    justify-content: center;
    align-items: center;
    color: white;
    z-index: 1000;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
`
const Popup = styled.div`
    width: 96%;
    padding: 40px;
    text-align: center;
    max-width: 600px;
    color: black;
    border-radius: 15px

    ${p => p.border && css`
        border: 2px solid  ${p.border}
    `}
`
// const
