import React, { useContext } from 'react';
import { Layout } from '../../commons';

import styled from 'styled-components';

import { CartContext } from '../../context/cart';


export const Cart = () => {

    const {cartProducts, removeFromCart} = useContext(CartContext);

    return(
        <Layout>
            <h1>Cart</h1>
            {cartProducts.map(({ uniqueLocalId, name, price, image }) => (
                <Container key={ uniqueLocalId }>
                    <img src={image} alt={name} />
                    <CartDescription>
                        <h3>{name}</h3>
                        <h4>{price}</h4>
                        <button onClick={() => removeFromCart(uniqueLocalId)}>Remove From Cart</button>
                    </CartDescription>
                </Container>
            ))}
        </Layout>
    )
}

const Container = styled.div`
    width: 500px;
    padding: 20px;
    border-radius: 10px;
    margin-right: 10px;
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.2);
    text-decoration: none;
    display: flex;
    margin-bottom: 40px;

    img {
        width: 100px;
        height: 100px;
        
        border-radius: 5px;
    }
`

const CartDescription = styled.div`

`
